triangle = int(input("Enter sides of traingle : "))
block = int(input("Enter no. of blocks : "))

# 138 is size of terminal

for y in range(block): # Block Times
    initspace = 2  # Iinitial space of each line
    for x in range(triangle): # Upper triangle
        if x+1 < 10:
            space = " "*(138-(x+1)*2)
        else:
            space = " "*(abs(138-(x+1)*2-2) )  # Taking abs for negative value when no is greater than screen size*2
        if x+1 > 68:
            print("{}{}{}{}".format(" " * (x + 1-initspace), x + 1, space, x + 1))
            initspace +=2
        else:
            print("{}{}{}{}".format(" " * (x + 1), x + 1, space, x + 1))

    initspace -= 4 # Decrement it to 4 beacuse lower traingle will be satrt from less 1 number
    for x in range(triangle-1, 0, -1): # Lower Triangle
        if x < 10:
            space = " "*(138-(x)*2)
        else:
            space = " "*(abs(138-(x)*2-2))
        if x > 68:
            print("{}{}{}{}".format(" " * (x - initspace), x, space, x))
            initspace -= 2
        else:
            print("{}{}{}{}".format(" " * (x), x, space, x))

